class SampleWork extends React.Component {


  constructor(props) {
    super(props);
    this.state = {photoIndex: 0,
      isOpen: false,value: 'th'};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {

    this.setState({value: event.target.value});
  }


  handleSubmit(event) {

    event.preventDefault();

  }


	render(){
	const { photoIndex, isOpen } = this.state;		

  var role = this.state.value;
  return (

    <div className="about">


        {isOpen && (
          <Lightbox
            mainSrc={images[photoIndex]}
            nextSrc={images[(photoIndex + 1) % images.length]}
            prevSrc={images[(photoIndex + images.length - 1) % images.length]}
            onCloseRequest={() => this.setState({ isOpen: false })}
            onMovePrevRequest={() =>
              this.setState({
                photoIndex: (photoIndex + images.length - 1) % images.length,
              })
            }
            onMoveNextRequest={() =>
              this.setState({
                photoIndex: (photoIndex + 1) % images.length,
              })
            }
          />
        )}

      <div className="container">
		<h1>Sample Work</h1>
        <div className="row">
					<div className="col-md-12">
						<form onSubmit={this.handleSubmit}>
						<label> 
							Select site from list to see screenshots: 
						</label>	
							<select style={{marginLeft: '10px'}} value={this.state.value} onChange={this.handleChange}>
								<option value="th">Fuel Management Systems</option>
								<option value="cc">Community Cafe</option>
								<option value="ge">Sample Room</option>
								<option value="se">Starling Eyewear</option>
							</select>

						</form>
						<br/><br/>
						<button type="button" onClick={() => this.setState({ isOpen: true })}>
          View Images Larger
        </button>
					</div>
				</div>


				{(() => {
					if (role === "th") {
					  return (
						<Tuthill/>
					  )
					} else if (role === "cc") {
					  return (
						<CoopersHawk/>
					  )
					} else if (role === "ge") {
					  return (
						<GoldEagle/>
					  )

					} else if (role === "se") {
					  return (
						<StarlingEyewear/>
					  )
					}
				})()}

			</div>
      </div>

  );
}}

